import React, { useState, useEffect, useRef } from 'react';

const Start = () => {
    const [text, setText] = useState('awesome');
    const [color, setColor] = useState('blue');
    const adjectives = ["awesome", "user-friendly", "interactive", "fun", "intuitive"];
    const colors = ["green", "pink", "blue"];
    const indexRef = useRef(0);

    useEffect(() => {
        const updateTextAndColor = () => {
            const i = indexRef.current;
            const newText = adjectives[i % adjectives.length];
            const newColor = colors[i % colors.length];
            setText(newText);
            setColor(newColor);
            indexRef.current = i + 1;
            setTimeout(updateTextAndColor, 1500);
        };

        const timeoutId = setTimeout(updateTextAndColor, 1500);

        return () => clearTimeout(timeoutId);
    }, [adjectives, colors]);

    return (
        <div id='titleScreen'>
            <p>Hello, I am</p>
            <h1 className='BLACKOUT'>tyra woden</h1>
            <p>I have a passion for creating <span className={color}>{text}</span> digital experiences.</p>
        </div>
    );
}

export default Start;
